/*** 

====================================================================
	Reset
====================================================================

 ***/
 
* {
	margin:0px;
	padding:0px;
	border:none;
	outline:none;
}

/*** 

====================================================================
	Global Settings
====================================================================

 ***/

body {
	font-family: 'Muli', sans-serif;
	font-size:14px;
	color:#777777;
	line-height:1.7em;
	font-weight:400;
	background:#ffffff;
	-webkit-font-smoothing: antialiased;
	-moz-font-smoothing: antialiased;
}

.bordered-layout .page-wrapper{
	padding:0px 50px 0px;
}

a{
	text-decoration:none;
	cursor:pointer;
	color:#9a7f59;
}

button,
a:hover,a:focus,a:visited{
	text-decoration:none;
	outline:none !important;
}

h1,h2,h3,h4,h5,h6 {
	position:relative;
	font-weight:normal;
	margin:0px;
	background:none;
	line-height:1.6em;
	font-family: 'Poppins', sans-serif;
}

/* Typography */

h1{
	font-size:44px;
}

h2{
	font-size:34px;
}

h3{
	font-size:28px;
}

h4{
	font-size:24px;
}

h5{
	font-size:20px;
}

h6{
	font-size:18px;
}

input,button,select,textarea{
	
}

textarea{
	overflow:hidden;
}

p{
	position:relative;
	line-height:1.8em;	
}

.auto-container{
	position:static;
	max-width:1200px;
	padding:0px 15px;
	margin:0 auto;
}

.medium-container{
	max-width:850px;
}

.page-wrapper{
	position:relative;
	margin:0 auto;
	width:100%;
	min-width:300px;
}

ul,li{
	list-style:none;
	padding:0px;
	margin:0px;	
}

img{
	display:inline-block;
	max-width:100%;
}

.theme-btn{
	display:inline-block;
	transition:all 0.3s ease;
	-moz-transition:all 0.3s ease;
	-webkit-transition:all 0.3s ease;
	-ms-transition:all 0.3s ease;
	-o-transition:all 0.3s ease;
}

.centered{
	text-align:center;	
}

/*Btn Style One*/

.btn-style-one{
	position:relative;
	padding:10px 72px 10px 30px;
	line-height:24px;
	background:#9a7f59;
	color:#ffffff;
	font-size:14px;
	cursor: pointer;
	font-weight:400;
	border-radius:50px;
	border:2px solid #9a7f59;
	text-transform:capitalize;
	font-family: 'Muli', sans-serif;
}

.btn-style-one .icon{
	position:absolute;
	right:-2px;
	top:-2px;
	color:#ffffff;
	width:57px;
	height:48px;
	line-height:48px;
	text-align:center;
	font-weight:400;
	font-size:16px;
	padding-right:4px;
	background-color:#9a7f59;
	border-radius:0px 50px 50px 0px;
}

.btn-style-one:hover{
	background:none;
	border-color:#9a7f59;
	color:#9a7f59;
}

/*Btn Style Two*/

.btn-style-two{
	position:relative;
	padding:12px 38px 12px;
	line-height:24px;
