/* Play Now */

.play-now {
	position: relative;
	left: 0;
	top: 0;
	height: 100%;
	width: 100%;
	z-index: 999;
	-webkit-transition: all 300ms ease;
	-moz-transition: all 300ms ease;
	-ms-transition: all 300ms ease;
	-o-transition: all 300ms ease;
	transition: all 300ms ease;
}

.play-now .icon{
    position: absolute;
    left: 50%;
    top: 50%;
    height: 70px;
    width: 70px;
    text-align: center;
    line-height: 70px;
    padding-left: 5px;
    background-color: #2e3cf4;
    color: #ffffff;
    z-index: 1;
    font-size: 19px;
    display: block;
    -webkit-transform: translate(-50%, -50%);
    -moz-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    -o-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    -ms-border-radius: 50%;
    -o-border-radius: 50%;
    border-radius: 50%;
    -webkit-box-shadow: 0 5px 10px 0 rgba(46, 60, 244, .3);
    -moz-box-shadow: 0 5px 10px 0 rgba(46, 60, 244, .3);
    -ms-box-shadow: 0 5px 10px 0 rgba(46, 60, 244, .3);
    -o-box-shadow: 0 5px 10px 0 rgba(46, 60, 244, .3);
    box-shadow: 0 5px 10px 0 rgba(46, 60, 244, .3);
    -webkit-transform-origin:center; 
    transform-origin:center; 
}

.play-now .ripple,
.play-now .ripple:before,
.play-now .ripple:after {
    position: absolute;
    top: 50%;
    left: 50%;
    height: 70px;
    width: 70px;
    -webkit-transform: translate(-50%, -50%);
    -moz-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    -o-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    -ms-border-radius: 50%;
    -o-border-radius: 50%;
    border-radius: 50%;
    -webkit-box-shadow: 0 0 0 0 rgba(46, 60, 244, .6);
    -moz-box-shadow: 0 0 0 0 rgba(46, 60, 244, .6);
    -ms-box-shadow: 0 0 0 0 rgba(46, 60, 244, .6);
    -o-box-shadow: 0 0 0 0 rgba(46, 60, 244, .6);
    box-shadow: 0 0 0 0 rgba(46, 60, 244, .6);
    -webkit-animation: ripple 3s infinite;
    -moz-animation: ripple 3s infinite;
    -ms-animation: ripple 3s infinite;
    -o-animation: ripple 3s infinite;
    animation: ripple 3s infinite;
}

.play-now .ripple:before {
    -webkit-animation-delay: .9s;
    -moz-animation-delay: .9s;
    -ms-animation-delay: .9s;
    -o-animation-delay: .9s;
    animation-delay: .9s;
    content: "";
    position: absolute;
}

.play-now .ripple:after {
    -webkit-animation-delay: .6s;
    -moz-animation-delay: .6s;
    -ms-animation-delay: .6s;
    -o-animation-delay: .6s;
    animation-delay: .6s;
    content: "";
    position: absolute;
}

@-webkit-keyframes ripple {
    70% {box-shadow: 0 0 0 70px rgba(255, 255, 255, 0);}
    100% {box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);}
}

@keyframes ripple {
    70% {box-shadow: 0 0 0 70px rgba(255, 255, 255, 0);}
    100% {box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);}
}

